        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵtext"](187, "ADB statistician Arturo Martinez, Jr. draws on pilot studies in the Philippines and Thailand to explain how innovative poverty maps could help better target resources to tackle poverty and inequality. See a ");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementStart"](188, "a", 33);
