        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementStart"](174, "p", 5);
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵtext"](175, "Learn how, in partnership with the Bill & Melinda Gates Foundation and ADB, Mongolia has developed safe ways of processing and reusing sludge from wastewater plants for agriculture and land reclamation. ");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementEnd"]();
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementStart"](176, "h4", 30);
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵtext"](177, " Making Cities");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelement"](178, "br");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵtext"](179, "More Livable ");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementEnd"]();
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementStart"](180, "p");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵtext"](181, " During 2020, ADB responded to the challenges of rapid urbanization in East Asia by helping to improve inefficient public transport systems, ensure access to clean water supply, and prevent flooding in urban areas by introducing integrated solutions that can be replicated in the region. ");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementEnd"]();
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementStart"](182, "p");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵtext"](183, "In the PRC, the bank signed a $200 million sector development program (investment and policy loan) to help transform Xiangtan from a carbon-intensive, heavily polluting city to a low-carbon, resilient, smart, and livable city. The project will install medians and peak-hour bus priority lanes on more than 60 kilometers of urban roads, make bus stops safer, and provide digital bus information and improved walking and cycling facilities for seamless access. The program will benefit 100,000 residents (50% female and 2,000 low-income residents) and encourage behavior changes toward a lower-carbon lifestyle. ");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementEnd"]();
