        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementStart"](151, "p");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵtext"](152, "ADB responded without delay by providing the necessary funds to establish a new molecular polymerase chain reaction (PCR) laboratory at the Colombo East Base Hospital. ");
