        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementStart"](186, "p");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵtext"](187, " When a 7.8 magnitude earthquake hit Nepal in April of 2015, the scale of the destruction was heartbreaking. Among the widespread devastation, which included the loss of nearly 500,000 homes, close to 30,000 classrooms were destroyed or damaged. ");
        _angular_core__WEBPACK_IMPORTED_MODULE_0__["ɵɵelementEnd"]();
